import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
 
  constructor(){
    super();
       this.state ={
      articles: [],
      loading: false
    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=aff95238a7594fa4be1462d1291821a9";
    let data = await fetch(url)
    let parsedData = await data.json()
    console.log(parsedData)
    this.setState({aticles: parsedData.articles})
  }
  render() {
    return (
      <div className='container my-3'>
        <h2>NewsMonkey - Top Hedlines</h2>
        <div className="row">
          {this.state.aricles.map((element)=>{
            return  <div className="col-md-4" key={element.url}>
            <NewsItem title= {element.title?element.title.slice(0, 44): ""} description= {element.description?element.description.slice(0, 88):""} imageUrl = {element.urlToImage} newsUrl = {element.url}/>
          </div>
           })}
        </div>
      </div>
    )
  }
}

export default News
